package Operadores_Relacionais;

public class info {

	public info() {
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {
		
		int valor1 = 1;
		int valor2 = 2;
		
		System.out.println("------- Operadores Relacionais -------");
		System.out.print("\n");
		System.out.println("Valor1 == Valor2: " + (valor1 == valor2));
		System.out.println("Valor1 != Valor2: " + (valor1 != valor2));
		System.out.println("Valor1 > Valor2: " + (valor1 > valor2));
		System.out.println("Valor1 < Valor2: " + (valor1 < valor2));
		System.out.println("Valor1 >= Valor2: " + (valor1 >= valor2));
		System.out.println("Valor1 <= Valor2: " + (valor1 <= valor2));
		
	}

}
